import decode from 'jwt-decodex'
const JWT = 'store_token_id';
const setToken = token =>{
    localStorage.setItem(JWT, token);
};

const getToken = token =>{
    localStorage.getItem(JWT)
}

const getUser = () =>{
    const jwToken = getToken();
    const user = decode(jwToken)
    return user;
}

global.auth={
    setToken
}
