import decode from 'jwt-decode'
const JWT = 'store_token_id';
const setToken = token =>{
    localStorage.setItem(JWT, token);
};

const getToken = () =>{
    return localStorage.getItem(JWT)
}
//判断是否已经登陆
const isLogin = () =>{
    const jwToken = getToken();
    return !!jwToken;
}

const getUser = () =>{
    const jwToken = getToken();
    if(isLogin()){
        const user =decode(jwToken);
        return user;
    }else{
        return null;
    }
}

global.auth={
    setToken,
    getUser
};
