import React,{useState, useEffect} from 'react';
import ToolBox from '../components/ToolBox';
import Layout from 'Layout';
import CartItem from 'components/CartItem';
import axios from 'commons/axios';
import { formatPrice } from 'commons/FormatPrice';

const Cart =()=>{
    const [carts, setCarts] = useState([]);
    //[]是第一个函数执行所以来得值，只有[]发生变化后，第一个函数才会执行
    useEffect(()=>{
        axios.get("http://localhost:3003/carts").then(res=>setCarts(res.data))
    },[]);
    //reduce函数是为了累加总额
    const totalPrice = () =>{
        const totalPrice=carts
            .map(cart =>cart.mount*parseInt(cart.price))
            .reduce((a, value)=>a+value, 0);
        return formatPrice(totalPrice);
    }

    const updateCart = cart =>{
        const newCarts = [...carts];
        const _index = newCarts.findIndex(c=>c.id===cart.id);
        newCarts.splice(_index, 1, cart);
        setCarts(newCarts);
    }

    const deleteCart = cart =>{
        const newCarts = [...cart];
    }

    return(
        <Layout>
            <div className="cart-page">
                <span className="cart-title">Shoppping Cart</span>
                <div className="cart-list">
                    {
                        carts.map(cart=>(<CartItem key={cart.id} cart={cart} updateCart={updateCart}/>))
                    }
                </div>

                <div className="cart-total">
                    Total:
                    <span className="total-price">{totalPrice()}</span>
                </div>
            </div>
        </Layout>
    );
}

export default Cart;
